package java_200_middle;

public class _34 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int temp=99;
		if(temp%2 ==1)
			//연산 결과가 참(true)이면 실행
			temp=temp*3+1;
		else
			//연산 결과가 거짓(false)이면 실행
			temp=temp/2;
		System.out.println("계산 후 = " + temp);
		
	}

}
